mapper.insert:
INSERT INTO Asset_model
(
model_code, model_name, model_type, model_manufacturer, model_size, model_spec1,
model_spec2, model_spec4, model_spec5, model_spec6, model_replaycement_type
)
VALUES
(
#{modelCode}, #{modelName}, #{modelType}, #{modelManufacturer}, #{modelSize}, #{modelSpec1},
#{modelSpec2}, #{modelSpec4}, #{modelSpec5}, #{modelSpec6}, #{modelReplaycementType}
)

mapper.update:
UPDATE Asset_info SET asset_number = #{assetNumber}, asset_model_code = #{assetModelCode}, asset_model_name = #{assetModelName}, asset_serial_number = #{assetSerialNumber},
asset_arrival_date = #{assetArrivalDate}, asset_payment_date = #{assetPaymentDate}, asset_payment_member_id = #{assetPaymentMemberId}, asset_payment_department = #{assetPaymentDepartment}, asset_payment_member_name = #{assetPaymentMemberName}, asset_payment_member_rank = #{assetPaymentMemberRank}, asset_payment_details = #{assetPaymentDetails}, asset_wired_mac_address = #{assetWiredMacAddress}, asset_wireless_mac_address = #{assetWirelessMacAddress},
status_type = #{statusType}, status_asset_status = #{statusAssetStatus}, status_member_id = #{statusMemberId}, status_asset_usage = #{statusAssetUsage}, status_asset_etc1 = #{statusAssetEtc1}, status_asset_spec1 = #{statusAssetSpec1}, status_asset_spec2 = #{statusAssetSpec2},
status_asset_due_diligence = #{statusAssetDueDiligence}, status_asset_etc2 = #{statusAssetEtc2},
asset_last_update_date = NOW(), asset_duration = #{assetDuration}
WHERE asset_number = #{assetNumber}

mapper.select:
SELECT * FROM asset_info LEFT JOIN hmm_member ON asset_info.status_member_id=hmm_member.member_id LEFT JOIN asset_model ON asset_info.asset_model_name=asset_model.model_name LEFT JOIN orderby_asset ON asset_info.status_type=orderby_asset.asset_status LEFT JOIN orderby_rank ON hmm_member.member_rank=orderby_rank.member_rank
WHERE status_type = #{statusType} OR status_asset_usage = #{statusAssetUsage}
order by orderby_asset.asset_grant, orderby_rank.member_grant, hmm_member.member_id, asset_info.status_asset_usage DESC
LIMIT #{startNo}, #{length};
